/**
 * Emergency Handler Function Node for Arizona Plumbing Pros
 * ---------------------------------------------------------
 * If the GPT classification tells us it's an Emergency, build
 * the emergency payload; otherwise just pass the data along.
 */

function processPlumbingEmergency(items) {
  return items.map(item => {
    const json = item.json;

    // 1) Pull from your "extractedData" if you have it, otherwise
    //    fall back to the raw GPT content (choices[0].message.content).
    const raw = json.extractedData
      ?? (json.choices?.[0]?.message?.content)
      ?? {};

    const IntentName       = raw.IntentName;
    const RequestedService = raw.RequestedService  || 'Plumbing Emergency';
    const Name             = raw.Name             || 'Unknown';
    const Phone            = raw.Phone            || 'N/A';
    const Address          = raw.Address          || 'N/A';
    const EmergencySeverity= Number(raw.EmergencySeverity) || 3;

    // 2) If it's not Emergency, skip this function entirely
    if (IntentName !== 'Emergency') {
      return item; 
    }

    // 3) Build your emergency details object
    const timestamp = new Date().toISOString();
    const labels = {
      5: 'CRITICAL - Immediate Response Required',
      4: 'URGENT - High Priority',
      3: 'STANDARD EMERGENCY',
      2: 'PRIORITY SERVICE',
      1: 'EXPEDITED SERVICE'
    };
    const severityLabel = labels[EmergencySeverity] || labels[3];

    const emergencyDetails = {
      timestamp,
      customer: { name: Name, phone: Phone, address: Address },
      issue: {
        service: RequestedService,
        severity: EmergencySeverity,
        severityLabel,
      },
      responseStatus: {
        notified: false,
        acknowledged: false,
        dispatchStatus: 'pending'
      }
    };

    // 4) Customer‚Äêfacing message
    let customerMessage = 
      `Arizona Plumbing Pros: Thank you for reporting your ${RequestedService} emergency. `;
    if (EmergencySeverity >= 4) {
      customerMessage +=
        "We understand this is URGENT. A technician will contact you IMMEDIATELY. ";
      customerMessage +=
        "If this is life-threatening, please evacuate and call 911.";
    } else if (EmergencySeverity === 3) {
      customerMessage +=
        "Our team will reach out shortly to address it.";
    } else {
      customerMessage +=
        "A technician will contact you soon to schedule an appointment.";
    }
    if (Phone !== 'N/A') {
      customerMessage += ` We‚Äôll call you at ${Phone}.`;
    } else {
      customerMessage +=
        " Please provide your phone number so we can reach you.";
    }

    // 5) Technician dispatch message
    let techMsg = `‚ö†Ô∏è EMERGENCY SERVICE REQUEST - ${severityLabel} ‚ö†Ô∏è\n\n`;
    techMsg += `Issue: ${RequestedService}\n`;
    techMsg += `Customer: ${Name}\n`;
    techMsg += `Phone: ${Phone}\n`;
    techMsg += `Address: ${Address}\n`;
    techMsg += `Reported at: ${new Date(timestamp).toLocaleString()}\n\n`;
    if (EmergencySeverity >= 4) {
      techMsg += "ACTION: IMMEDIATE RESPONSE NEEDED";
    } else if (EmergencySeverity === 3) {
      techMsg += "ACTION: Contact within 15 minutes";
    } else {
      techMsg += "ACTION: Contact within 30 minutes";
    }

    // 6) Should we initiate a call? (you can disable call logic later)
    const callRequired = EmergencySeverity >= 4;

    // 7) Return a brand-new item with all the bits you need downstream
    return {
      json: {
        ...json,
        // convenience copies
        problemType: RequestedService,
        severity: EmergencySeverity,
        severityLabel,
        // our emergency payload
        emergencyDetails,
        customerMessage,
        technicianMessage: techMsg,
        callRequired,
        timestamp
      }
    };
  });
}

